
from threading import local

class IAStore:

    _ctf_integrated_app_thread_local = local()
    
    def get_integrated_app_support(self):
        return getattr(IAStore._ctf_integrated_app_thread_local, "value", None)
  
    def set_integrated_app_support(self, value):
        IAStore._ctf_integrated_app_thread_local.value = value
